import React from 'react';
// CSS
import './NavBarComp.css';
// Dependencies
import { Link } from 'react-router-dom';
import { useDispatch, useSelector } from 'react-redux';
import * as actionTypes from '../redux/constants/cartConstants';

const NavBarComp = ({ history, click }) => {
	const cart = useSelector(state => state.cart);
	const { cartItems } = cart;

	const dispatch = useDispatch();

	const getCartCount = () => {
		return cartItems.reduce((cartCount, item) => Number(item.quantity) + cartCount, 0);
	};

	const logoutFunc = () => {
		dispatch({ type: actionTypes.RESET_CART });
		history.push('/login');
	};

	return (
		<nav className="navbar">
			{/* Website headline logo */}
			<div className="navbar-logo">
				<Link to="/" className="navbar-links" style={{ display: 'flex' }}>
					<h2> Shopit! eCommerce Website</h2>
				</Link>
			</div>

			{/* Cart link */}
			<Link to="/cart" className="navbar-links">
				<div className="cart-link">
					{/* Cart counter */}
					<span className="cart-counter">{getCartCount()}</span>
					{/* Cart Icon */}
					<div className="cartlogo">
						<i className="fas fa-shopping-cart" />
					</div>
				</div>
				<div>
					<button className="logout-btn" onClick={logoutFunc}>
						<Link>LOGOUT</Link>
					</button>
				</div>
			</Link>

			{/* Hamburger Menu */}
			<div className="hamburger-menu" onClick={click}>
				<div />
				<div />
				<div />
			</div>
		</nav>
	);
};

export default NavBarComp;
