import * as actionTypes from '../constants/accountConstants';

export const getAccountsReducer = (state = { accounts: [] }, action) => {
	switch (action.type) {
		case actionTypes.GET_ACCOUNTS_REQUEST: {
			return {
				loading: true,
				products: []
			};
		}
		case actionTypes.GET_ACCOUNTS_SUCCESS: {
			return {
				loading: false,
				accounts: action.payload
			};
		}
		case actionTypes.GET_ACCOUNTS_FAIL: {
			return {
				loading: false,
				error: action.payload
			};
		}
		default: {
			return { ...state };
		}
	}
};

export const getAccountCartReducer = (state = { accountCart: [] }, action) => {
	switch (action.type) {
		case actionTypes.GET_ACCOUNT_CART_REQUEST: {
			return {
				loading: true,
				accountCart: []
			};
		}
		case actionTypes.GET_ACCOUNT_CART_SUCCESS: {
			return {
				loading: false,
				accountCart: action.payload
			};
		}
		case actionTypes.GET_ACCOUNT_CART_FAIL: {
			return {
				loading: false,
				error: action.payload
			};
		}
		default: {
			return { ...state };
		}
	}
};
