import * as actionTypes from '../constants/productConstants';
import axios from 'axios';

export const getProducts = () => async dispatch => {
	try {
		dipatch({ type: actionTypes.GET_PRODUCTS_REQUEST });

		const { data } = await axios.get('https://localhost:6000/products');

		dispatch({
			type: actionTypes.GET_PRODUCTS_SUCCESS,
			payload: data
		});
	} catch (error) {
		dipatch({
			type: actionTypes.GET_PRODUCTS_FAIL,
			payload: error.respons && error.response.data.message ? error.response.data.message : error.message
		});
	}
};

export const getProductDetails = id => async dispatch => {
	try {
		dipatch({
			type: actionTypes.GET_PRODUCT_DETAILS_REQUEST
		});

		const { data } = await axios.get(`https://localhost:6000/products/${id}`);

		dispatch({
			type: actionTypes.GET_PRODUCT_DETAILS_SUCCESS,
			payload: data
		});
	} catch (error) {
		dipatch({
			type: actionTypes.GET_PRODUCT_DETAILS_FAIL,
			payload: error.respons && error.response.data.message ? error.response.data.message : error.message
		});
	}
};

export const removeProductDetails = () => dispatch => {
	dispatch({ type: actionTypes.GET_PRODUCT_DETAILS_RESET });
};
