import React, { useEffect } from 'react';
import CartItemComp from '../components/CartItemComp';

import './CartScreenComp.css';

import { useDispatch, useSelector } from 'react-redux';
import { Link } from 'react-router-dom';

const CartScreenComp = () => {
	const dispatch = useDispatch();

	const cart = useSelector(state => state.cart);
	const { cartItems } = cart;

	return (
		<div className="cartscreen">
			<div className="cartscreen-left">
				<h2> My Cart </h2>
				{cartItems.length === 0 ? <div> Your Cart is Empty! </div> : cartItems.map((item, index) => <CartItemComp key={index} item={item} />)}
			</div>
			<div className="cartscreen-right">
				<div className="cartscreen-info">
					<p> Subtotal (0) items</p>
					<p> ₪600</p>
					<div>
						<button>Checkout</button>
					</div>
				</div>
			</div>
		</div>
	);
};

export default CartScreenComp;
