import * as actionTypes from '../constants/accountConstants';
import axios from 'axios';

export const getAccounts = () => async dispatch => {
	try {
		dispatch({ type: actionTypes.GET_ACCOUNTS_REQUEST });
		let { data } = await axios.get('http://localhost:4000/accounts');
		dispatch({
			type: actionTypes.GET_ACCOUNTS_SUCCESS,
			payload: data
		});
	} catch (error) {
		dispatch({
			type: actionTypes.GET_ACCOUNTS_FAIL,
			payload: error.response && error.response.data.message ? error.response.data.message : error.message
		});
	}
};

export const addAccount = account => async dispatch => {
	try {
		dispatch({ type: actionTypes.POST_ACCOUNT_REQUEST });
		let { data } = await axios.post('http://localhost:4000/accounts/', account);
		dispatch({
			type: actionTypes.POST_ACCOUNT_SUCCESS,
			payload: data
		});
	} catch (error) {
		dispatch({
			type: actionTypes.POST_ACCOUNT_FAIL,
			payload: error.response && error.response.data.message ? error.response.data.message : error.message
		});
	}
};

export const getAccountCart = id => async (dispatch, getState) => {
	try {
		dispatch({ type: actionTypes.LOAD_ACCOUNT_CART_REQUEST });
		let { data } = await axios.get(`http://localhost:4000/accounts/${id}`);
		dispatch({
			type: actionTypes.LOAD_ACCOUNT_CART_SUCCESS,
			payload: [ ...data.cart ]
		});
		localStorage.setItem('cart', JSON.stringify(getState().cart.cartItems));
	} catch (error) {
		dispatch({
			type: actionTypes.LOAD_ACCOUNT_CART_FAIL,
			payload: error.response && error.response.data.message ? error.response.data.message : error.message
		});
	}
};

export const addToCart = (productId, quantity, account) => async (dispatch, getState) => {
	try {
		dispatch({ type: actionTypes.ADD_TO_CART_REQUEST });
		const { data } = await axios.get(`http://localhost:4000/products/${productId}`);

		account.cart.push({
			productId: data._id,
			name: data.name,
			imageUrl: data.imageUrl,
			price: data.price,
			countInStock: data.countInStock,
			quantity
		});
		let accountToUpdate = {
			username: account.username,
			password: account.password,
			cart: [ ...account.cart ]
		};
		await axios.put(`http://localhost:4000/accounts/${account._id}`, [ ...account.cart ]);

		dispatch({
			type: actionTypes.ADD_TO_CART_SUCCESS,
			payload: {
				productId: data._id,
				name: data.name,
				imageUrl: data.imageUrl,
				price: data.price,
				countInStock: data.countInStock,
				quantity
			}
		});

		localStorage.setItem('cart', JSON.stringify(getState().cart.cartItems));
	} catch (error) {
		dispatch({
			type: actionTypes.ADD_TO_CART_FAIL,
			payload: error.response && error.response.data.message ? error.response.data.message : error.message
		});
	}
};

export const removeFromCart = (productId, account) => async (dispatch, getState) => {
	try {
		dispatch({ type: actionTypes.REMOVE_FROM_CART_REQUEST });

		await axios.put(`http://localhost:4000/accounts/${account._id}`, [ ...account.cart.filter(product => product.productId !== productId) ]);
		dispatch({
			type: actionTypes.REMOVE_FROM_CART_SUCCESS,
			payload: productId
		});

		localStorage.setItem('cart', JSON.stringify(getState().getAccounts.loggedUser.cart));
	} catch (error) {
		dispatch({
			type: actionTypes.REMOVE_FROM_CART_FAIL,
			payload: error.response && error.response.data.message ? error.response.data.message : error.message
		});
	}
};
