// React Hooks
import React, { useEffect, useState } from 'react';
// CSS
import './BackdropComp.css';

const BackdropComp = props => {
	const [ classStr, setClassStr ] = useState('backdrop');
	useEffect(
		() => {
			if (!props.show) {
				setClassStr('');
			}
		},
		[ props ]
	);
	return <div className={classStr} />;
};

export default BackdropComp;
