import * as actionTypes from '../constants/accountConstants';

export const getAccountsReducer = (state = { accounts: [], loggedUser: {} }, action) => {
	switch (action.type) {
		case actionTypes.POST_ACCOUNT_REQUEST: {
			return {
				loading: true,
				accounts: [ ...state.accounts ]
			};
		}
		case actionTypes.POST_ACCOUNT_SUCCESS: {
			return {
				loading: false,
				accounts: [ ...state.accounts, action.payload ]
			};
		}
		case actionTypes.POST_ACCOUNT_FAIL: {
			return {
				loading: false,
				error: action.payload
			};
		}
		case actionTypes.LOGIN_ACCOUNT: {
			localStorage.setItem('loggedUser', JSON.stringify(action.payload));
			return {
				loggedUser: action.payload
			};
		}
		case actionTypes.ADD_TO_CART_REQUEST: {
			return {
				loading: true,
				loggedUser: { ...state.loggedUser }
			};
		}
		case actionTypes.ADD_TO_CART_SUCCESS: {
			const item = action.payload;
			const existInCart = state.loggedUser.cart.find(x => x.productId === item.productId);
			if (existInCart) {
				return {
					loading: false,
					loggedUser: { ...state.loggedUser, cart: state.loggedUser.cart.map(x => (x.productId === existInCart.productId ? item : x)) }
				};
			}
			else {
				return {
					loading: false,
					loggedUser: { ...state.loggedUser, cart: [ ...state.loggedUser.cart, item ] }
				};
			}
		}
		case actionTypes.ADD_TO_CART_FAIL: {
			return {
				loading: false,
				error: action.payload
			};
		}
		case actionTypes.REMOVE_FROM_CART_REQUEST: {
			return {
				loading: true,
				loggedUser: { ...state.loggedUser }
			};
		}
		case actionTypes.REMOVE_FROM_CART_SUCCESS: {
			return {
				...state,
				loading: false,
				loggedUser: { ...state.loggedUser, cart: action.payload }
			};
		}
		case actionTypes.REMOVE_FROM_CART_FAIL: {
			return {
				loading: false,
				loggedUser: { ...state.loggedUser },
				error: action.payload
			};
		}
		case actionTypes.LOGOUT_ACCOUNT: {
			localStorage.setItem('loggedUser', {});
			return {
				loggedUser: {},
				accounts: [ ...state.accounts ]
			};
		}
		// case actionTypes.RESET_CART: {
		// 	localStorage.setItem('cart', []);
		// 	return {
		// 		loading: false,
		// 		cartItems: []
		// 	};
		// }
		default: {
			return { ...state };
		}
	}
};
