import * as actionTypes from '../constants/cartConstants';
import axios from 'axios';

export const addToCart = (productId, quantity, account) => async (dispatch, getState) => {
	const { data } = await axios.get(`http://localhost:4000/products/${productId}`);
	account.cart.push(data);
	let accountToUpdate = {
		username: account.username,
		password: account.password,
		cart: [ ...account.cart ]
	};
	await axios.put(`http://localhost:4000/accounts/${id}`, accountToUpdate);
	dispatch({
		type: actionTypes.ADD_TO_CART,
		payload: {
			productId: data._id,
			name: data.name,
			imageUrl: data.imageUrl,
			price: data.price,
			countInStock: data.countInStock,
			quantity
		}
	});

	localStorage.setItem('cart', JSON.stringify(getState().cart.cartItems));
};

export const removeFromCart = id => (dispatch, getState) => {
	dispatch({
		type: actionTypes.REMOVE_FROM_CART,
		payload: id
	});

	localStorage.setItem('cart', JSON.stringify(getState().cart.cartItems));
};
