import * as actionTypes from '../constants/accountConstants';

export const getAccountsReducer = (state = { accounts: [], loggedUser: {} }, action) => {
	switch (action.type) {
		case actionTypes.GET_ACCOUNTS_REQUEST: {
			return {
				loading: true,
				loggedUser: { ...state.loggedUser },
				products: []
			};
		}
		case actionTypes.GET_ACCOUNTS_SUCCESS: {
			return {
				loading: false,
				loggedUser: { ...state.loggedUser },
				accounts: action.payload
			};
		}
		case actionTypes.GET_ACCOUNTS_FAIL: {
			return {
				loading: false,
				loggedUser: { ...state.loggedUser },
				error: action.payload
			};
		}
		case actionTypes.POST_ACCOUNT_REQUEST: {
			return {
				loading: true,
				loggedUser: {},
				accounts: action.payload
			};
		}
		default: {
			return { ...state };
		}
	}
};
