import * as actionTypes from '../constants/accountConstants';

export const getAccountsReducer = (state = { accounts: [], loggedUser: {} }, action) => {
	switch (action.type) {
		case actionTypes.GET_ACCOUNTS_REQUEST: {
			return {
				loading: true,
				loggedUser: localStorage.getItem('loggedUser') ? localStorage.getItem('loggedUser') : {},
				accounts: []
			};
		}
		case actionTypes.GET_ACCOUNTS_SUCCESS: {
			return {
				loading: false,
				loggedUser: localStorage.getItem('loggedUser') ? localStorage.getItem('loggedUser') : {},
				accounts: action.payload
			};
		}
		case actionTypes.GET_ACCOUNTS_FAIL: {
			return {
				loading: false,
				loggedUser: localStorage.getItem('loggedUser') ? localStorage.getItem('loggedUser') : {},
				error: action.payload
			};
		}
		case actionTypes.POST_ACCOUNT_REQUEST: {
			return {
				loading: true,
				accounts: [ ...state.accounts ]
			};
		}
		case actionTypes.POST_ACCOUNT_SUCCESS: {
			return {
				loading: false,
				accounts: [ ...state.accounts, action.payload ]
			};
		}
		case actionTypes.POST_ACCOUNT_FAIL: {
			return {
				loading: false,
				error: action.payload
			};
		}
		case actionTypes.LOGIN_ACCOUNT: {
			localStorage.setItem('loggedUser', JSON.stringify(action.payload));
			return {
				loggedUser: action.payload,
				accounts: [ ...state.accounts ]
			};
		}
		case actionTypes.LOGOUT_ACCOUNT: {
			localStorage.setItem('loggedUser', {});
			return {
				loggedUser: {},
				accounts: [ ...state.accounts ]
			};
		}
		default: {
			return { ...state };
		}
	}
};
